package org.hypergraphdb.viewer.event;import java.util.EventObject;import org.hypergraphdb.viewer.FEdge;import org.hypergraphdb.viewer.FNode;import org.hypergraphdb.viewer.GraphView;/** * The event source must be the GraphView that changed. */public abstract class GraphViewChangeEvent extends EventObject{	public static final int NODES_ADDED_TYPE = 1;	public static final int EDGES_ADDED_TYPE = 2;	public static final int NODES_REMOVED_TYPE = 4;	public static final int EDGES_REMOVED_TYPE = 8;	public static final int NODES_SELECTED_TYPE = 16;	public static final int NODES_UNSELECTED_TYPE = 32;	public static final int EDGES_SELECTED_TYPE = 64;	public static final int EDGES_UNSELECTED_TYPE = 128;	public GraphViewChangeEvent(GraphView source)	{		super(source);	}	/**	 * The Object returned is a HGVNetwork 	 */	public final Object getSource()	{		return super.getSource();	}	public abstract int getType();	public abstract boolean isNodesAddedType();	public abstract boolean isEdgesAddedType();	public abstract boolean isNodesRemovedType();	public abstract boolean isEdgesRemovedType();	public abstract FNode[] getAddedNodes();	public abstract FEdge[] getAddedEdges();	public abstract FNode[] getRemovedNodes();	public abstract FEdge[] getRemovedEdges();} 