package fing.model;import java.util.EventObject;/** *  * The event source must be the RootGraph that changed. *  */public abstract class RootGraphChangeEvent extends EventObject{	public RootGraphChangeEvent(FRootGraph source)	{		super(source);	}	public static final int NODES_CREATED_TYPE = 1;	public static final int EDGES_CREATED_TYPE = 2;	public static final int NODES_REMOVED_TYPE = 4;	public static final int EDGES_REMOVED_TYPE = 8;	public static final int META_RELATIONSHIP_NODES_CREATED_TYPE = 16;	public static final int META_RELATIONSHIP_EDGES_CREATED_TYPE = 32;	public static final int META_RELATIONSHIP_NODES_REMOVED_TYPE = 64;	public static final int META_RELATIONSHIP_EDGES_REMOVED_TYPE = 128;	public abstract int getType();	public abstract boolean isNodesCreatedType();	public abstract boolean isEdgesCreatedType();	public abstract boolean isNodesRemovedType();	public abstract boolean isEdgesRemovedType();		public abstract int[] getCreatedNodeIndices();	public abstract int[] getCreatedEdgeIndices();	public abstract int[] getRemovedNodeIndices();	public abstract int[] getRemovedEdgeIndices();	} // abstract class RootGraphChangeEvent