package phoebe.event;import java.util.EventObject;import fing.model.FGraphPerspective;/** *  * The event source must be the GraphPerspective that changed. *  */public abstract class GraphPerspectiveChangeEvent extends EventObject{	public static final int NODES_RESTORED_TYPE = 1;	public static final int EDGES_RESTORED_TYPE = 2;	public static final int NODES_HIDDEN_TYPE = 4;	public static final int EDGES_HIDDEN_TYPE = 8;	public static final int NODES_SELECTED_TYPE = 16;	public static final int NODES_UNSELECTED_TYPE = 32;	public static final int EDGES_SELECTED_TYPE = 64;	public static final int EDGES_UNSELECTED_TYPE = 128;	/**	 * 	 * The source parameter should be either a RootGraph or a GraphPerspective;	 * see specification of getSource().	 * @see #getSource()	 */	public GraphPerspectiveChangeEvent(FGraphPerspective source)	{		super(source);	}	/**	 * The Object returned is a GraphPerspective 	 */	public final Object getSource()	{		return super.getSource();	}	public abstract int getType();	public abstract boolean isNodesRestoredType();	public abstract boolean isEdgesRestoredType();	public abstract boolean isNodesHiddenType();	public abstract boolean isEdgesHiddenType();			/** The integer values in the returned array are RootGraph indices. */	public abstract int[] getRestoredNodeIndices();	/** The integer values in the returned array are RootGraph indices. */	public abstract int[] getRestoredEdgeIndices();	/** The integer values in the returned array are RootGraph indices. */	public abstract int[] getHiddenNodeIndices();	/** The integer values in the returned array are RootGraph indices. */	public abstract int[] getHiddenEdgeIndices();} // abstract class GraphPerspectiveChangeEvent